# encoding:utf-8from util.trainlog import train_log_writefrom model.trainfunction import train_task, init_train_config# ---------------------train function-----------------------#def start_train(train_config):    init_train_config(train_config)    train_task()# ---------------------train input config-----------------------#def structure_input_train_config(global_var):    init_train_config = dict()    init_train_config['config_train_path'] = global_var.get('config_train_path')    init_train_config['config_global_path'] = global_var.get('config_global_path') # defect info    init_train_config['datasets_path'] = global_var.get('datasets_path')    init_train_config['logs_train_path'] = global_var.get('logs_train_path')    return init_train_config