# encoding:utf-8# 读 三个配置文件  global、train、deploydef read_config_centent(config_path):    """    :param config_path: 配置文件路径    :return: 文件内容    """    config_centent = None    try:        with open(config_path, "r", encoding="utf-8") as f:            config_centent = f.read()    except:        pass    return config_centent# 覆盖 三个配置文件  global、train、deploydef write_config_centent(config_path, content):    """    :param config_path: 配置文件路径    :param content: 写入覆盖内容    :return: Success：True/False    """    _isSuccess = False    # \r 转为 空    content = content.replace("\r", "")    try:        with open(config_path, "w", encoding="utf-8") as f:            f.write(content)        _isSuccess = True    except:        pass    return _isSuccess